ALGORITHME CalculMoyenne
VARIABLES
    note1, note2, note3: REEL
    moyenne: REEL
DEBUT
    LIRE(note1);
    LIRE(note2);
    LIRE(note3);
    moyenne ←  (note1 + note2 + note3) / 3
    ECRIRE("La moyenne est:", moyenne);
FIN

ALGORITHME Addition
VARIABLES
    a, b, somme : Entiers
DÉBUT
    // Saisie des deux nombres
    Écrire("Premier nombre : ");
    Lire(a);

    Écrire("Deuxième nombre : ");
    Lire(b);

    // Calcul
    somme ← a + b;

    // Affichage
    Écrire("La somme de ", a, " et ", b, " est ", somme);
FIN

ALGORITHME ExempleVariables
VARIABLES
    nom: CHAINE
    age: ENTIER
    taille: REEL
    estEtudiant: BOOLEEN
DEBUT
    nom  ←  "Marie";
    age  ←  20;
    taille  ←  1.65;
    estEtudiant  ←  VRAI;
    age ←  20 + 5;

    ECRIRE("Nom:", nom);
    ECRIRE("Age dans 5 ans:", age);
    ECRIRE("Taille:", taille);
    ECRIRE("Etudiant:", estEtudiant);
FIN

